import { useState } from "react";

const Form = props => {
    const [inputValue, setInputValue] = useState('');
    const [error, setError] = useState(''); 

    const handleChange = (event) =>{
            setInputValue( event.target.value)
    }

    const handleSubmit = (event) =>{
        event.preventDefault();
        let filteredValue = inputValue.replace(/\D/, '');
         if(filteredValue >=1 && filteredValue<=100){
            props.onSubmitForm(inputValue);
         }else{
            setError("Enter an id between 1 and 100"); 
         }

    }

    return(
        <form onSubmit={handleSubmit}>
             <input type="text" value ={inputValue} onChange={handleChange} placeholder="Enter the album id" />
              <input type="submit" value="Get Album Photos"/>
               <h2>{error}</h2>
        </form>
    )

}

export default Form;
